import React from 'react'
import { ShoppingBag } from 'components/AllSvgIcon'
import Button from 'components/Button/Button'
import { openModal, closeModal } from '@redq/reuse-modal'
import { useCart } from 'contexts/cart/use-cart';
import { CartItem } from 'containers/Cart/CartItem/CartItem';

export default function CartButton() {
  const { isOpen, cartItemsCount, toggleCart, calculatePrice } = useCart();

  const handleModal = () => {
    openModal({
      show: true,
      config: {
        className: 'cartPopup',
        width: 'auto',
        height: 'auto',
        enableResizing: false,
        disableDragging: true,
        transition: {
          tension: 360,
          friction: 40,
        },
      },
      closeOnClickOutside: true,
      component: CartItem,
      closeComponent: () => <div />,
      componentProps: { onCloseBtnClick: closeModal, scrollbarHeight: 370 },
    });
  };

  return (
    <>
      <Button
        onClick={handleModal}
        style={{padding:"10px", marginRight:"15px"}}
        size="small"
        title={<ShoppingBag/>}
        pointer={1}
      />
    </>
  )
}
