import React, { useContext, useEffect, useState } from 'react';
import { NextPage } from 'next';
import { useQuery } from '@apollo/react-hooks';
import { Modal } from '@redq/reuse-modal';
import { withApollo } from 'helper/apollo';
import { SEO } from 'components/seo';
import Checkout from 'containers/CheckoutWithSidebar/CheckoutWithSidebar';
import { GET_LOGGED_IN_CUSTOMER } from 'graphql/query/customer.query';
import { openModal } from '@redq/reuse-modal';

import { ProfileProvider } from 'contexts/profile/profile.provider';
import Router, { useRouter } from 'next/router';
import { AuthContext } from 'contexts/auth/auth.context';
import AuthenticationForm from 'containers/SignInOutForm/Form';

type Props = {
  deviceType: {
    mobile: boolean;
    tablet: boolean;
    desktop: boolean;
  };
};
const CheckoutPage: NextPage<Props> = ({ deviceType }) => {
  const router = useRouter();
  const { authDispatch, 
    authState: { isAuthenticated }, } = useContext<any>(AuthContext);
  const handleLogout = () => {
    if (typeof window !== 'undefined') {
      localStorage.removeItem('access_token');
      authDispatch({ type: 'SIGN_OUT' });
      Router.push('/');
    }
  };
  const [isAuth, setIsAuth] = useState(false);
  useEffect(()=>{
    if(!isAuthenticated) {
      handleLogout();
      setIsAuth(false);
    } else{
      setIsAuth(true);
    }
  },[])
  if(isAuth){
    return null;
  }
  const { data, error, loading } = useQuery(GET_LOGGED_IN_CUSTOMER);
  if (loading) {
    return <div style={{margin:"150px 0 0 0"}}>loading...</div>;
  }
  // if(error) {
    // handleLogout()
    // handleJoin()
    // Router.push('/');
    // return null
  // }

  if (error) return <div style={{margin:"150px 0 0 0"}}>{error.message}</div>;
  const token = 'true';

  return (
    <>
      <SEO title='Checkout - PickBazar' description='Checkout Details' />
      <ProfileProvider initData={data.me}>
        <Modal>
          <Checkout token={token} deviceType={deviceType} />
        </Modal>
      </ProfileProvider>
    </>
  );
};

export default withApollo(CheckoutPage);
