import React from 'react';
import Card from './Card';
import { CloseIcon } from '../AllSvgIcon';
import { DeleteButton, Wrapper } from './PaymentCard.style';

interface PaymentCardProps {
  className?: string;
  logo: string;
  alt: string;
  cardNumber: string;
  name: string;
  color?: string;
}

const PaymentCard: React.FunctionComponent<PaymentCardProps & any> = ({
  className,
  onChange,
  onDelete,
  name,
  id,
  cardType,
  lastFourDigit,
  Type,
  color,
}) => {
  function handleChange() {
    onChange();
  }
  function handleDelete() {
    onDelete();
  }
  const {firstName, lastName} = name;
  return (
    <Wrapper
      htmlFor={`payment-card-${id}`}
      className={`payment-card-radio ${className ? className : ''}`}
    >
      <input
        type='radio'
        id={`payment-card-${id}`}
        name={firstName + " " + lastName}
        value={`payment-card-${id}`}
        onChange={handleChange}
        checked={Type === 'primary'}
      />

      <Card
        id={`card-${id}`}
        cardType={cardType}
        lastFourDigit={lastFourDigit}
        color={color}
        name={firstName + " " + lastName}
      />

      <DeleteButton
        // type="submit"
        onClick={handleDelete}
        className='card-remove-btn'
      >
        <CloseIcon />
      </DeleteButton>
    </Wrapper>
  );
};

export default PaymentCard;
