import gql from 'graphql-tag';

export const CREATE_CONTACT = gql`
  mutation(
    $number: String !
  ){
    createContact(
      number:$number
      type:"secondary"
    ){
      contact{
        id
        number
        Type
      }
    }
  }
`

export const UPDATE_CONTACT = gql`
  mutation(
    $slug: String!
    $number: String!
  ) {
    updateContact(
      slug:$slug
      number:$number
      type:"secondary"
    ){
      contact{
        slug
        id
        number
        Type
      }
    }
  }
`;
export const DELETE_CONTACT = gql`
  mutation($slug: String!) {
    deleteContact(slug: $slug) {
      ok
    }
  }
`;
