import React, { useEffect } from 'react'
import AuthenticationForm from 'containers/SignInOutForm/Form'
import { AuthContext } from 'contexts/auth/auth.context';
import { openModal, Modal } from '@redq/reuse-modal';
import { withApollo } from 'helper/apollo';

import Button from 'components/Button/Button';
import LoginSvg from '../image/Mobile-login-bro.svg';
import styled from 'styled-components';

const Container = styled.div`
  display: flex;
  justify-content: center;
  /* width: 990px; */
  margin: 0 auto;
  min-height: 100vh;
  padding-top: 150px;
`
const Col = styled.div`
  width: 50%;
  padding: 0 15px;
`
const DesctopImg = styled.div`
  width: 50%;
  padding: 0 15px;
  @media screen and (max-witdth: 990px){
    display: none;
  }
`
const ImgContainer = styled.div`
  overflow: hidden;
  position: relative;
  width: 100%;
  img.LoginForm{
    width: 100%;
  }
`

function Login() {
  const {
    authState: { isAuthenticated, currentForm },
    authDispatch,
  } = React.useContext<any>(AuthContext);

  const onJoin = ()=>{
    authDispatch({
      type: 'SIGNIN',
    });
    openModal({
      show: true,
      overlayClassName: 'quick-view-overlay',
      closeOnClickOutside: true,
      component: AuthenticationForm,
      closeComponent: '',
      config: {
        enableResizing: false,
        disableDragging: true,
        className: 'quick-view-modal',
        width: 458,
        height: 'auto',
      },
    });
  }
  return (
    <div>
      {/* <Container>
        <DesctopImg>
          <ImgContainer>
            <img className="LoginSvg" src={LoginSvg} alt="login-bro"/>
          </ImgContainer>
        </DesctopImg>
        <Col>
          <AuthenticationForm/>
        </Col>
      </Container> */}
      <Col>
        <AuthenticationForm/>
      </Col>
      <Modal/> 
    </div>
  )
}

export default withApollo(Login)
