import React, { useContext } from 'react';
import styled from 'styled-components';
import { themeGet } from '@styled-system/theme-get';
import * as Yup from 'yup';
import { closeModal } from '@redq/reuse-modal';
import { FormikProps, ErrorMessage, Formik, Form } from 'formik';
import { useMutation } from '@apollo/react-hooks';
import MaskedInput from 'react-text-mask';
import { ProfileContext } from 'contexts/profile/profile.context';
import Button from 'components/Button/Button';
import { UPDATE_CONTACT, CREATE_CONTACT } from 'graphql/mutation/contact';
import { FieldWrapper, Heading } from './Update.style';

type Props = {
  item?: any | null;
};
// Shape of form values
type FormValues = {
  id?: number | null;
  type?: string;
  name: string;
  info: string;
};

const ContactValidationSchema = Yup.object().shape({
  name: Yup.string().required('Number is required'),
  info: Yup.string().required('Number is required'),
});

const UpdateAddress: React.FC<Props> = ({ item }) => {
  const initialValues = {
    id: item.id || null,
    type: item.type || 'secondary',
    name: item.name || "",
    info: item.info || "",
  };
  const [addContactMutation] = useMutation((item && item.id) ?  UPDATE_CONTACT : CREATE_CONTACT);
  const { 
    // state, 
  dispatch } = useContext(ProfileContext);
  const handleSubmit = async (values: FormValues, { setSubmitting }: any) => {
    console.log(values)
    await addContactMutation({
      variables: {
        info: values.info,
        name: values.name,
      },
    });
    // console.log(values, 'formik values');
    dispatch({ type: 'ADD_OR_UPDATE_CONTACT', payload: values });
    closeModal();
    setSubmitting(false);
  };
  return (
    <Formik
      initialValues={initialValues}
      onSubmit={handleSubmit}
      validationSchema={ContactValidationSchema}
    >
      {({
        values,
        handleChange,
        handleBlur,
        isSubmitting,
      }: FormikProps<FormValues>) => (
        <Form>
          <Heading>
            {item && item.id ? 'Edit Contact' : 'Add New Contact'}
          </Heading>
          <FieldWrapper>
            <MaskedInput
              mask={[
                '(',
                /[1-9]/,
                /\d/,
                /\d/,
                ')',
                ' ',
                /\d/,
                /\d/,
                /\d/,
                '-',
                /\d/,
                /\d/,
                /\d/,
                /\d/,
                /\d?/,
                /\d?/,
                /\d?/,
                /\d?/,
                /\d?/,
                /\d?/,
              ]}
              className='form-control'
              placeholder='Enter a phone number'
              guide={false}
              id='my-input-id'
              value={values.number}
              onChange={handleChange}
              onBlur={handleBlur}
              name='number'
              render={(ref: any, props: {}) => (
                <StyledInput ref={ref} {...props} />
              )}
            />
          </FieldWrapper>
          <ErrorMessage name='number' component={StyledError} />

          <Button
            disabled={isSubmitting}
            type='submit'
            title='Save Contact'
            size='medium'
            fullwidth={true}
          />
        </Form>
      )}
    </Formik>
  );
};

export default UpdateAddress;

const StyledInput = styled.input`
  width: 100%;
  height: 54px;
  border-radius: 6px;
  font-family: ${themeGet('colors.fontFamily', 'Lato, sans-serif')};
  border: 1px solid ${themeGet('colors.borderColor', '#e6e6e6')};
  color: ${themeGet('colors.darkBold', '#0D1136')};
  font-size: 16px;
  line-height: 19px;
  font-weight: 400;
  padding: 0 18px;
  box-sizing: border-box;
  transition: border-color 0.25s ease;

  &:hover,
  &:focus {
    outline: 0;
  }

  &:focus {
    border-color: ${themeGet('colors.primary', '#009e7f')};
  }

  &::placeholder {
    color: ${themeGet('colorsdarkRegular', '#77798C')};
  }
`;

const StyledError = styled.div`
  color: red;
  padding-bottom: 10px;
  margin-top: -5px;
`;
