import React, { useEffect, useState } from 'react'
import { ShoppingBag } from 'components/AllSvgIcon'
import Button from 'components/Button/Button'
import { useCart, CartProvider, getLocalCart } from 'contexts/cart/use-cart';
import Router from 'next/router';

const CartButton = () => {
  const { 
    // isOpen, 
    cartItemsCount, 
    toggleCart, 
    // calculatePrice 
    // getCartItemsCount,
    refreshCart
  } = useCart();
  const handleClick = ()=>{
    refreshCart()
    const path = Router.router.pathname;
    toggleCart()
    if(path === '/' || path === '/product/[slug]')return;
    Router.push('/');
  }
  const [a, setA] = useState(true)
  useEffect(() => {
    if(typeof window !== "undefined" && globalThis === window){
      refreshCart()
      setA(false);
    }
  }, [])
  // console.log(getCartItemsCount)     
  return (
    <Button
      className='product-cart'
      onClick={handleClick}
      style={{padding:"15px", marginRight:"15px"}}
      size="small"
      icon={<ShoppingBag/>}
      // pointer={cartItemsCount}
      pointer={cartItemsCount}
      intlButtonId="qwerty"
      />
  )
}
export default (props)=>(
  <CartProvider {...props}>
    <CartButton {...props}/>
  </CartProvider>
);
