import React from 'react'
import { ShoppingBag } from 'components/AllSvgIcon'
import Button from 'components/Button/Button'
import { openModal, closeModal } from '@redq/reuse-modal'
import { useCart, CartProvider } from 'contexts/cart/use-cart';
import { Modal } from '@redq/reuse-modal';
import CartModal from 'containers/Cart/Cart';
import { withApollo } from 'helper/apollo';

const CartButton = () => {
  const { isOpen, cartItemsCount, toggleCart, calculatePrice } = useCart();

  const handleModal = () => {
    openModal({
      show: true,
      config: {
        className: 'cartPopup',
        width: 'auto',
        height: 'auto',
        enableResizing: false,
        disableDragging: true,
        transition: {
          tension: 360,
          friction: 40,
        },
      },
      closeOnClickOutside: true,
      component: CartModal,
      closeComponent: () => <div />,
      componentProps: { onCloseBtnClick: closeModal, scrollbarHeight: 370 },
    });
  };

  return (
    <CartProvider>
      <Button
        onClick={handleModal}
        style={{padding:"10px", marginRight:"15px"}}
        size="small"
        icon={<ShoppingBag/>}
        pointer={cartItemsCount}
        intlButtonId="qwerty"
        />
    </CartProvider>
  )
}
export default (CartButton);
