import React from 'react';
// import NavLink from 'components/NavLink/NavLink';
import Button from 'components/Button/Button';
import Popover from 'components/Popover/Popover';
// import { OFFER_PAGE, HELP_PAGE } from 'constants/navigation';
import { AuthorizedMenu } from '../AuthorizedMenu';
import LanguageSwitcher from '../LanguageSwitcher/LanguageSwitcher';
// import { HelpIcon } from 'components/AllSvgIcon';
import { RightMenuBox } from './RightMenu.style';
import CartButton from './CartButton';
// import { ShoppingBag } from '../../../../../components/AllSvgIcon'

type Props = {
  onLogout: () => void;
  onJoin: () => void;
  avatar: string;
  isAuthenticated: boolean;
};

export const RightMenu: React.FC<Props> = ({
  onLogout,
  avatar,
  isAuthenticated,
  onJoin
}) => {
  return (
    <RightMenuBox>
      {/* <NavLink
        className="menu-item"
        href={OFFER_PAGE}
        label="Offer"
        intlId="navlinkOffer"
      />
      <NavLink
        className="menu-item"
        href={HELP_PAGE}
        label="Need Help"
        intlId="navlinkHelp"
        iconClass="menu-icon"
        icon={<HelpIcon />}
      /> */}
      {/* <LanguageSwitcher /> */}

      <CartButton/>

      {!isAuthenticated ? (
        <Button
          onClick={onJoin}
          size="small"
          title="Login"
          style={{ fontSize: 15, color: '#fff' }}
          intlButtonId="joinButton"
        />
      ) : (
        <Popover
          direction="right"
          className="user-pages-dropdown"
          handler={<img src={avatar} alt="user" />}
          content={<AuthorizedMenu onLogout={onLogout} />}
        />
      )}
    </RightMenuBox>
  );
};
