import React, { useEffect } from 'react'
import AuthenticationForm from 'containers/SignInOutForm/Form'
import { AuthContext } from 'contexts/auth/auth.context';
import { openModal, Modal } from '@redq/reuse-modal';
import { withApollo } from 'helper/apollo';

import LoginSvg from '../image/Mobile-login-bro.svg';
import styled from 'styled-components';

const Container = styled.div`
  display: flex;
  width: 990px;

  img.LoginSvg{
    width: 45%;
  }
`

function Login() {
  const {
    authState: { isAuthenticated, currentForm },
    authDispatch,
  } = React.useContext<any>(AuthContext);

  useEffect(()=>{
    authDispatch({
      type: 'SIGNIN',
    });
    openModal({
      show: true,
      overlayClassName: 'quick-view-overlay',
      closeOnClickOutside: true,
      component: AuthenticationForm,
      closeComponent: '',
      config: {
        enableResizing: false,
        disableDragging: true,
        className: 'quick-view-modal',
        width: 458,
        height: 'auto',
      },
    });
  },[])
  return (
    <div>
      <img className="LoginSvg" src={LoginSvg} alt="login-bro"/>
      <Modal/>
    </div>
  )
}

export default withApollo(Login)
