import React, { useContext } from "react";
import SignInForm from "./SignIn";
import SignOutForm from "./SignUp";
import ForgotPassForm from "./ForgotPass";
import SignUpSuccessForm from './SignUpSuccess';
import { AuthContext } from "contexts/auth/auth.context";

export default function AuthenticationForm({isNotModal}) {
  const { authState } = useContext<any>(AuthContext);
  switch(authState.currentForm){
    case 'signIn':
      return <SignInForm isNotModal={isNotModal}/>;
    case 'loading':
    case 'signUp':
      return <SignOutForm isNotModal={isNotModal}/>;
    case 'signUpSuccess':
      return <SignUpSuccessForm isNotModal={isNotModal}/>;
    case 'forgotPass':
      return <ForgotPassForm isNotModal={isNotModal}/>;
  }
  return <SignInForm isNotModal={isNotModal}/>;
}
